mapper.sql:
SELECT ni.*, ui.nickname
  FROM notification_info ni LEFT JOIN user_info ui ON ni.senderUserNo = ui.no
 WHERE ni.userNo = #{userNo}
   AND ni.gubun = #{gubun}

mapper.update:
UPDATE notification_info
SET checkYn='Y'
WHERE notifiNo = #{notifiNo}

