mapper.select:
SELECT *
  FROM notification_info
 WHERE userNo = #{userNo}
   AND gubun = #{gubun}

mapper.update:
UPDATE notification_info
   SET checkYn='Y'
 WHERE notifiNo = #{notifiNo}

mapper.select:
SELECT COUNT(*)
  FROM ( SELECT *
           FROM notification_info
          WHERE userNo = #{userNo}
            AND gubun = #{gubun}
        )
