mapper.select:
select count(*) from( select p.prodNo,p.prodName,p.prodPrice,p.address,p.tradState,count(i.prodNo) as 'interestCnt',f.dbFileName from product_info p left join interest_info i on p.prodNo = i.prodNo left join file f on p.prodNo = f.no left join hash_tag h on p.prodNo = h.prodNo left join category_info c on p.categoryNo = c.categoryNo
and (p.prodName like concat('%',#{param.tag},'%') or p.address like concat('%',#{param.tag},'%') or h.tagName like concat('%',#{param.tag},'%')) and p.delYn='N' and p.tradeKind='0' and f.fileOrder='0' and (p.tradState='0' or p.tradState='2') and f.gubun='2'
and (p.prodName like concat('%',#{body.search},'%') or p.address like concat('%',#{body.search},'%') or h.tagName like concat('%',#{body.search},'%')) and p.delYn='N' and p.tradeKind='0' and f.fileOrder='0' and (p.tradState='0' or p.tradState='2') and f.gubun='2'
and p.delYn='N' and p.tradeKind='0' and f.fileOrder='0' and (p.tradState='0' or p.tradState='2') and f.gubun='2' and p.categoryNo=#{param.category}
and p.delYn='N' and p.tradeKind='0' and f.fileOrder='0' and (p.tradState='0' or p.tradState='2') and f.gubun='2' and c.LargeCateNo=#{param.largeCateNo}
and p.delYn='N' and p.tradeKind='0' and f.fileOrder='0' and (p.tradState='0' or p.tradState='2') and f.gubun='2' and c.LargeCateNo=#{param.largeCateNo} and c.MediumCateNo=#{param.mediumCateNo}
and p.delYn='N' and p.tradeKind='0' and f.fileOrder='0' and (p.tradState='0' or p.tradState='2') and f.gubun='2' and c.LargeCateNo=#{param.largeCateNo} and c.MediumCateNo=#{param.mediumCateNo} and c.SmallCateNo=#{param.smallCateNo}
and p.delYn='N' and p.tradeKind='0' and f.fileOrder='0' and (p.tradState='0' or p.tradState='2') and f.gubun='2'
group by p.prodNo,p.prodName,p.prodPrice,p.address,p.tradState,f.dbFileName)T

select count(*) from( select p.prodNo,p.prodName,p.prodPrice,p.address,p.tradState,count(i.prodNo) as 'interest',f.dbFileName from product_info p left join interest_info i on p.prodNo = i.prodNo left join file f on p.prodNo = f.no left join hash_tag h on p.prodNo = h.prodNo left join category_info c on p.categoryNo = c.categoryNo
and (p.prodName like concat('%',#{param.tag},'%') or p.address like concat('%',#{param.tag},'%') or h.tagName like concat('%',#{param.tag},'%')) and p.delYn='N' and p.tradeKind='1' and f.fileOrder='0' and (p.tradState='1' or p.tradState='2') and f.gubun='2'
and (p.prodName like concat('%',#{body.search},'%') or p.address like concat('%',#{body.search},'%') or h.tagName like concat('%',#{body.search},'%')) and p.delYn='N' and p.tradeKind='1' and f.fileOrder='0' and (p.tradState='1' or p.tradState='2') and f.gubun='2'
and p.delYn='N' and p.tradeKind='1' and f.fileOrder='0' and (p.tradState='1' or p.tradState='2') and f.gubun='2' and categoryNo=#{param.category}
and p.delYn='N' and p.tradeKind='1' and f.fileOrder='0' and (p.tradState='1' or p.tradState='2') and f.gubun='2' and c.LargeCateNo=#{param.largeCateNo}
and p.delYn='N' and p.tradeKind='1' and f.fileOrder='0' and (p.tradState='1' or p.tradState='2') and f.gubun='2' and c.LargeCateNo=#{param.largeCateNo} and c.MediumCateNo=#{param.mediumCateNo}
and p.delYn='N' and p.tradeKind='1' and f.fileOrder='0' and (p.tradState='1' or p.tradState='2') and f.gubun='2' and c.LargeCateNo=#{param.largeCateNo} and c.MediumCateNo=#{param.mediumCateNo} and c.SmallCateNo=#{param.smallCateNo}
and p.delYn='N' and p.tradeKind='1' and f.fileOrder='0' and (p.tradState='1' or p.tradState='2') and f.gubun='2'
group by p.prodNo,p.prodName,p.prodPrice,p.address,p.tradState,f.dbFileName)T

select p.prodNo,p.prodName,p.prodPrice,p.address,p.tradState,count(i.prodNo) as 'interestCnt',f.dbFileName from product_info p left join interest_info i on p.prodNo = i.prodNo left join file f on p.prodNo = f.no left join hash_tag h on p.prodNo = h.prodNo left join category_info c on p.categoryNo = c.categoryNo
and (p.prodName like concat('%',#{param.tag},'%') or p.address like concat('%',#{param.tag},'%') or h.tagName like concat('%',#{param.tag},'%')) and p.delYn='N' and p.tradeKind='0' and f.fileOrder='0' and (p.tradState='0' or p.tradState='2') and f.gubun='2'
and (p.prodName like concat('%',#{body.search},'%') or p.address like concat('%',#{body.search},'%') or h.tagName like concat('%',#{body.search},'%')) and p.delYn='N' and p.tradeKind='0' and f.fileOrder='0' and (p.tradState='0' or p.tradState='2') and f.gubun='2'
and p.delYn='N' and p.tradeKind='0' and f.fileOrder='0' and (p.tradState='0' or p.tradState='2') and f.gubun='2' and p.categoryNo=#{param.category}
and p.delYn='N' and p.tradeKind='0' and f.fileOrder='0' and (p.tradState='0' or p.tradState='2') and f.gubun='2' and c.LargeCateNo=#{param.largeCateNo}
and p.delYn='N' and p.tradeKind='0' and f.fileOrder='0' and (p.tradState='0' or p.tradState='2') and f.gubun='2' and c.LargeCateNo=#{param.largeCateNo} and c.MediumCateNo=#{param.mediumCateNo}
and p.delYn='N' and p.tradeKind='0' and f.fileOrder='0' and (p.tradState='0' or p.tradState='2') and f.gubun='2' and c.LargeCateNo=#{param.largeCateNo} and c.MediumCateNo=#{param.mediumCateNo} and c.SmallCateNo=#{param.smallCateNo}
and p.delYn='N' and p.tradeKind='0' and f.fileOrder='0' and (p.tradState='0' or p.tradState='2') and f.gubun='2'
group by p.prodNo,p.prodName,p.prodPrice,p.address,p.tradState,f.dbFileName
order by p.prodPrice desc
order by p.prodPrice asc
order by count(i.prodNo) desc
order by p.viewCnt desc
order by p.prodName asc
order by p.regDt desc
LIMIT #{body.limitPage}, #{body.recordCount}

